export default class PubSub {
    events: {
        [x: string]: Function[];
    };
    constructor();
    /**
     * 订阅
     * @param event 事件名称
     * @param callback 事件回调
     */
    on(event: string, callback: Function): [] | number;
    /**
     * 发布
     * @param event 事件名称
     * @param data 载荷
     */
    emit<T>(event: string | number, ...data: T | any): any[];
    /**
     * 销毁事件
     * @param event 事件名称
     * @param callback 事件方法
     */
    off(event: string, callback: Function): void;
}
